<footer class="pt-24 pb-12 lg:py-24">
  <div class="container flex flex-col-reverse justify-between gap-8 !px-4 lg:flex-row">
    <div class="max-w-lg">
      @if (carbon_get_theme_option('footer_text'))
        <p class="text-gray">{{ carbon_get_theme_option('footer_text') }}</p>
      @endif

      <div class="mt-4 flex flex-row items-center gap-2">
        @foreach (['facebook', 'twitter', 'youtube', 'instagram', 'linkedin', 'vimeo'] as $account)
          @if (carbon_get_theme_option($account))
            <a rel="noopener" class="inline-block rounded-full bg-black p-1 text-white"
              aria-label="{{ $account }} link" href="{{ carbon_get_theme_option($account) }}" target="_blank">
              <x-dynamic-component :component="'icon.' . $account" class="mt-4" />
            </a>
          @endif
        @endforeach
        @if (carbon_get_theme_option('contact_email'))
          <a class="ml-4 inline-block font-bold"
            href="mailto:{{ carbon_get_theme_option('contact_email') }} ">{{ carbon_get_theme_option('contact_email') }}</a>
        @endif
      </div>
    </div>
